import ItemHolder from "./infoheader/ItemHolder";

const InfoHeader = (props) => {
  return (
    <div className="info-margin">
      <ItemHolder title="All Participants" amount={props.totalUsers} />
      <ItemHolder title="Joined in 24 hours" amount={props.totalUsers} />
      <ItemHolder title="Participant MATIC" amount={props.totalUsers * 300} />
      <ItemHolder
        title="Participant USD"
        amount={props.totalUsers * 0.25 * 130}
      />
      <style jsx>{`
        .info-margin {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
          grid-gap: 50px;

          margin: auto auto;
          margin-top: 5vh;
        }
      `}</style>
    </div>
  );
};

export default InfoHeader;
