import X3Matrix from "./X3matrix";
import React, { useState, useEffect } from "react";
import Spinner from "../Spinner.jsx";

const X3MatrixHolder = (props) => {
  const [reload, setReload] = useState(0);
  useEffect(() => {
    if (props.struc != undefined) {
      setReload(1);
    }
  });

  if (props.struc === undefined) {
    return (
      <div className="upper-holder">
        <div className="top-part">
          <h1>Forsage x3</h1>
        </div>
        <div className="bottom-part">
          <Spinner />
        </div>
        <p>Hallo ich bin der Lückenfüller Text für die Icones</p>
        <style jsx>{`
          .upper-holder {
            margin-bottom: 20px;
            text-align: left;
          }
          .top-part {
            width: 100%;
            background: rgba(28, 22, 85, 1);
            border-radius: 20px 20px 0 0;
            padding: 10px 10px;
          }
          .bottom-part {
            width: 100%;
            background: rgba(28, 22, 85, 1);
            border-radius: 0 0 20px 20px;
            padding: 10px 10px;
            border-top: 2px solid black;
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
            grid-gap: 30px;
          }
        `}</style>
      </div>
    );
  } else {
    return (
      <>
        <div className="upper-holder">
          <div className="top-part">
            <h1>Forsage x3</h1>
          </div>
          <div className="bottom-part">
            {props.struc.map((matrix, index) => (
              <X3Matrix
                id={matrix.id}
                key={matrix.key}
                cost={matrix.cost}
                active={matrix.number}
                bought={matrix.userX3Exist}
                account={props.account}
              />
            ))}
          </div>

          <p>Hallo ich bin der Lückenfüller Text für die Icones</p>
        </div>
        <style jsx>{`
          .upper-holder {
            margin-bottom: 20px;
            text-align: left;
          }
          .top-part {
            width: 100%;
            background: rgba(28, 22, 85, 1);
            border-radius: 20px 20px 0 0;
            padding: 10px 10px;
          }
          .bottom-part {
            width: 100%;
            background: rgba(28, 22, 85, 1);
            border-radius: 0 0 20px 20px;
            padding: 10px 10px;
            border-top: 2px solid black;
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
            grid-gap: 30px;
          }
        `}</style>
      </>
    );
  }
};
export default X3MatrixHolder;
