import React, { useRef, useState } from "react";

const UserContent = (props) => {
  const [copySuccess, setCopySuccess] = useState("");
  const inputRef = useRef(null);
  function copyToClipboard(e) {
    inputRef.current.select();
    document.execCommand("copy");
    e.target.focus();
    setCopySuccess("Copied!");
  }

  return (
    <>
      <div className="content-user">
        <h4>{props.title}</h4>
        <form>
          <input ref={inputRef} value={props.placeholder} readOnly />
        </form>
        <br />
        {
          /* Logical shortcut for only displaying the 
          button if the copy command exists */
          document.queryCommandSupported("copy") && (
            <div>
              <button className="copy" onClick={copyToClipboard}>
                Copy
              </button>
              <button className="etherscan">To Etherscan</button>
            </div>
          )
        }
        <div className="success">{copySuccess}</div>
      </div>

      <style jsx>{`
        h4 {
          margin-top: 5px;
        }
        .content-user {
          background: rgba(16, 1, 62, 1);
          width: 90%;
          padding: 10px 10px;
          margin: 20px auto;
          border-radius: 20px;
          text-align: center;
        }
        input {
          width: auto;
          margin: 10px auto;
          text-align: center;
          padding: 5px 10px;
          overflow: auto;
          border-radius: 10px;
          background: #271950;
          border: none;
          color: white;
        }
        input::placeholder {
        }
        .success {
          background: rgb(0, 237, 47);
          background: linear-gradient(
            306deg,
            rgba(0, 237, 47, 1) 29%,
            rgba(0, 179, 35, 1) 64%,
            rgba(0, 179, 35, 1) 83%
          );
          color: white;
          width: 50%;
          margin: 20px auto;
          font-style: bold;
        }
        button {
          margin: 10px 15px;
          border: none;
          color: white;
          border-radius: 10px;
          padding: 10px 25px;
        }
        .copy {
          background: rgb(31, 168, 255);
          background: linear-gradient(
            223deg,
            rgba(31, 168, 255, 1) 22%,
            rgba(19, 108, 255, 1) 47%
          );
        }
        .etherscan {
          background: #271950;
        }
      `}</style>
    </>
  );
};

export default UserContent;

