const Sidebar = (props) => {
  return (
    <div className="sidebar">
      <div className="holder">
        <p>
          ID: {props.userIds}
          <br />
          <br />
          {props.partnersCount}
          <br />
          <br />
        </p>
        <div className="content-goals"></div>
        <div classname="oneMillion"></div>
      </div>
      <div className="divider"></div>
      <div className="holder">
        <div className="content-goals"></div>
        <div className="content-goals"></div>
      </div>
      <div className="divider"></div>
      <div className="holder">
        <div className="content-user">
          <h4>Your referal Link:</h4>
          <input
            type="text"
            disabled="disabled"
            placeholder={"www.sonstwas.io" + props.userIds}
          />
          <br />
          <button className="copy">Copy</button>
          <button className="etherscan">Etherscan</button>
        </div>
        <div className="content-user">
          <h4>Your Ethereum Wallet Address</h4>
          <input type="text" disabled="disabled" placeholder={props.account} />
          <br />
          <button className="copy">Copy</button>
          <button className="etherscan">Etherscan</button>
        </div>
        <div className="content-user">
          <h4>smart contract address:</h4>
          <input type="text" disabled="disabled" placeholder={props.address} />
          <br />
          <button className="copy">Copy</button>
          <button className="etherscan">Etherscan</button>
        </div>
      </div>
      <style jsx>{`
        .sidebar {
          height: 100%;
          background: rgba(28, 22, 85, 1);
          border-radius: 20px;
          padding: 10px;
        }
        .holder {
          text-align: center;
          width: 90%;
          margin: 0 auto;
        }
        .divider {
          width: 90%;
          margin: 10px auto;
          background: grey;
          height: 1px;
        }
        input {
          width: auto;
          margin: 10px auto;
          text-align: center;
        }
        button {
          margin: 10px 15px;
        }
      `}</style>
    </div>
  );
};

export default Sidebar;
