// import { useRouter } from "next/router";
import { useRouter } from "next/router";
import { useContext } from "react";

const RefLink = () => {
  const router = useRouter();
  const id = router.query.id;
  console.log(id);
  // idToAddress
  // pass Address into Context
  async function loadBlockchainData() {
    const web3 = new Web3(Web3.givenProvider || "http://localhost:8545");
    const accounts = await web3.eth.getAccounts();
    this.setState({ account: accounts[0] });

    const contract = new web3.eth.Contract(ABI, ADDRESS);
    this.setState({ contract });
    const isExists = await contract.methods
      .isUserExists(this.state.account)
      .call();
    this.setState({ isExist: isExists });
    const costs = await contract.methods.registrationCost().call();
    this.setState({ cost: costs });
    this.setState({ isLoading: false });
  }

  async function loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum);
      await window.ethereum.enable();
    } else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider);
    } else {
      window.alert(
        "Non-Ethereum browser detected. You should consider trying MetaMask!"
      );
    }
  }

  async function register(_refererAddress) {
    this.setState({ loading: true });
    await this.loadWeb3();
    await this.loadBlockchainData();
    if (this.state.isExist) {
      Router.push("/dashboard");
      this.setState({ loading: false });
    } else {
      await this.state.contract.methods
        .registrationExt(_refererAddress)
        .send({
          value: this.state.cost,
          from: this.state.account,
        })
        .then(function (receipt) {
          Router.push("/dashboard");
          this.setState({ loading: false });
        });
    }
  }
  return (
    <>
      <p>Your inviter is ID {router.query.id}</p>
    </>
  );
};

export default RefLink;
