// import { useRouter } from "next/router";
import { useRouter } from "next/router";
import { useContext } from "react";

const RefLink = () => {
  const router = useRouter();
  const id = router.query.id;
  console.log(id);
  // idToAddress
  // pass Address into Context
  async function loadBlockchainData() {
    const web3 = new Web3(Web3.givenProvider || "http://localhost:8545");
    const accounts = await web3.eth.getAccounts();
    this.setState({ account: accounts[0] });

    const contract = new web3.eth.Contract(ABI, ADDRESS);
    this.setState({ contract });
    const isExists = await contract.methods
      .isUserExists(this.state.account)
      .call();
    this.setState({ isExist: isExists });
    const costs = await contract.methods.registrationCost().call();
    this.setState({ cost: costs });
    this.setState({ isLoading: false });
  }

  async function loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum);
      await window.ethereum.enable();
    } else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider);
    } else {
      window.alert(
        "Non-Ethereum browser detected. You should consider trying MetaMask!"
      );
    }
  }

  async function register(_refererAddress) {
    this.setState({ loading: true });
    await this.loadWeb3();
    await this.loadBlockchainData();
    if (this.state.isExist) {
      Router.push("/dashboard");
      this.setState({ loading: false });
    } else {
      await this.state.contract.methods
        .registrationExt(_refererAddress)
        .send({
          value: this.state.cost,
          from: this.state.account,
        })
        .then(function (receipt) {
          Router.push("/dashboard");
          this.setState({ loading: false });
        });
    }
  }
  return (
    <>
      <div className="header">
        <div className="hero-image">
          <div className="picture-holder"></div>
        </div>
        <div className="form-part">
          <h1>Register</h1>
          <p>Your inviter is ID {router.query.id}</p>
          <form
            className="automatic"
            onSubmit={async (event) => {
              event.preventDefault();
              await this.register(this.state.refererAddress);
              const newUser = this.state.account;
              setAccount(newUser);
            }}
          >
            <button className="auto">
              {/* {!isLoading ? <b>Authorisation</b> : <b>Loading</b>} */}
            </button>
          </form>

          <p>Telegram Channel @cryptoofficial</p>
        </div>
      </div>
      <style jsx>{`
        .header {
          width: 100vw;
          height: 100vh;
          overflow: hidden;
          background: rgba(16, 1, 62, 1);
          display: flex;
        }
        @media only screen and (max-width: 1000px) {
          .header {
            height: auto;
          }
        }
        .hero-image {
          width: 70vw;
          height: 100vw;
        }
        .picture-holder {
          width: 100%;
          height: 100vh;
          background-image: url("/assets/img/hero-picture.jpg");
          background-repeat: no-repeat;
          background-size: contain;
          background-position: center;
        }
        .hero {
          width: 100%;
        }
        .form-part {
          background: rgba(28, 22, 85, 1);
          width: 30vw;
          padding: 60px;
          color: white;
          height: 100vh;
          text-align: center;
        }

        .manual-btn {
          background: rgb(55, 214, 255);
          background: linear-gradient(
            223deg,
            rgba(55, 214, 255, 1) 0%,
            rgba(16, 83, 245, 1) 46%
          );
          box-shadow: none;
          outline: none;
          border: none;
        }
        input {
          width: 100%;
          background: rgba(16, 1, 62, 1);
          box-shadow: none;
          outline: none;
          border: none;
          padding: 20px 20px;
          border-radius: 30px;
          font-size: 1.2em;
          margin: 30px 0;
          color: grey;
        }

        p {
          color: grey;
        }
        h1 {
          margin-top: 15vh;
          margin-bottom: 5vh;
          font-size: 1.5em;
        }

        button {
          width: 100%;
          text-align: center;
          padding: 20px 0;
          border-radius: 30px;
          color: white;
          font-size: 1.2em;
        }
        @media only screen and (max-width: 1000px) {
          .hero-image {
            display: none;
          }
          .form-part {
            width: 95vw;
            margin: auto auto;
          }
        }
        .auto {
          background: rgb(0, 237, 47);
          background: linear-gradient(
            306deg,
            rgba(0, 237, 47, 1) 29%,
            rgba(0, 179, 35, 1) 64%,
            rgba(0, 179, 35, 1) 83%
          );
          box-shadow: none;
          outline: none;
          border: none;
        }
        button {
          width: 100%;
          text-align: center;
          padding: 20px 0;
          border-radius: 30px;
          color: white;
          font-size: 1.2em;
        }
      `}</style>
    </>
  );
};

export default RefLink;
